   * interface Exchange {
   *   // Buy NFT with the specificed state of `_tokenId`.
   *   function buy(uint256 _tokenId, uint256 _price, bytes calldata _kittyState) external;
   * }
   * ```
   */
  function stateOf(uint256 _tokenId) external view returns (bytes memory);
}

